    content = function(file) {
      png(file)
      print(plotInput())
      dev.off()
    }
  )

})

wfromt <- function(tt, s = 1, prior = "laplace", a = 0.5) {
#
#  Find the weight that has posterior median threshold tt, 
#   given s (sd) and a.
#
	pr <- substring(prior, 1, 1)
	if(pr == "l"){
	  tma <- tt/s - s*a
	  wi <- 1/abs(tma)
	  wi[tma > -35] <- pnorm(tma[tma > -35])/dnorm(tma[tma > -35])
          wi <- a * s * wi - beta.laplace(tt, s, a)
	}
	if(pr == "c") {
		dnz <- dnorm(tt)
		wi <- 1 + (pnorm(tt) - tt * dnz - 1/2)/
                          (sqrt(pi/2) * dnz * tt^2)
		wi[!is.finite(wi)] <- 1
	}
	return(1/wi)
}

spawn_fn<-function(nr) {
  env<-new.env()
  env$a<-nr
  j<-eval(quote(parallel::mcparallel(a)), envir = env)
  return(list(job=j, value=nr))
}

test_fn<-function(item) {
  ans<-parallel::mccollect(jobs = item$job)[[1]]
  if(length(ans)==0) {
    browser()
  }
  if(item$value!=ans) {
    stop("Error")
  }
}
a<-spawn_fn(1)
test_fn(a)

jobs<-rep(x = list(list()),200)

for(i in seq(10000)) {
  job_nr<-sample.int(length(jobs), 1)
  if(length(jobs[[job_nr]])!=0){
    test_fn(jobs[[job_nr]])
  }
  jobs[[job_nr]]<-spawn_fn(runif(1))
}

