#' Modul
#' 
#' Objects of this class represent the nodes in the tree of metadata objects. 
#' 
#' @param type Specify the type of object this metadata entry belongs to (e.g. Map, Data, Model)
#' @param attributes List of attributes
#' @param children List of child elements 
#' @return model object
#' @seealso \code{\link{mdw}}, \code{\link{type}}, \code{\link{attribute}}, \code{\link{children}}, \code{\link{summary.Modul}},  \code{\link{toXML}}, \code{\link{parseXML}}
#' @export
#' @examples
#' m1<-Modul(type = "Map",attributes= c(a="Map1",b="Map 2"))
#' m2<-Modul(type = "Map",attributes = c(a="nested Map"))
#' m3<-Modul(type = "Data",attributes= c(a="Data1",b="Data 2"),children = list(child1=m2))
#' 
#' m5<-Modul(type = "Map",attributes= c(a="Map3",b="Map4"))
#' 
#' m4<-Modul(type = "Model",attributes = c(a="Attribute1",b="Attribute 2"),
#'    children = list(child1=m1,child2=m3,child3=m5))
#' 
#' summary(m4)
#' 
#' \dontrun{
#' toXML(m4,file="xml/out.xml") # use variable name for topmost entry ("m4" in this case)
#'
#' toXML(m4,file="xml/out.xml",name="test") #set name for topmost entry 
#' }
#' 
Modul <- function(type=NULL,attributes=NULL,children=NULL){
  if(!is.null(type)){
    me <- list(
      type=type,
      attributes=attributes
    )

    if(!is.null(children)){
      me<-c(me,children)
    }

    class(me) <- append("Modul",class(me)) # Set the name for the class
    return(me)  
  } else {
    return(NULL)
  }
}


#' type
#' 
#' Gets type information of an object of class \code{\link{Modul}}.
#' 
#' @seealso \code{\link{Modul}}, \code{\link{attribute}}, \code{\link{children}}
#' @export
#' 
type <- function(x){
  UseMethod('type', x)
}

#' @describeIn type
#' @param x Object of class \code{\link{Modul}}
#' @return Getter Method: type
#' @export
#' 
type.Modul <- function(x){
  return(x$type)
}

#' type<-
#' 
#' Sets type information of an object of class \code{\link{Modul}}.
#' 
#' @rdname type
#' @export
#' 
'type<-' <- function(x,value){
  UseMethod('type<-', x)
}

#' @rdname type
#' @param value New value
#' @return Setter Method: object of class \code{\link{Modul}}
#' @export
#' @examples
#' m1 <- Modul(type="Map")    
#' type(m1)
#' type(m1) <- "Data"
#' type(m1)
#' 
'type<-.Modul' <- function(x, value){
  x$type <- value
  return(x)
}

#' attribute
#' 
#' Get a nodes attributes
#' 
#' @seealso \code{\link{Modul}}, \code{\link{type}}, \code{\link{children}}
#' @export
#' 
attribute <- function(x){
  UseMethod('attribute', x)
}

#' @describeIn attribute
# @param x Object of class \code{\link{Modul}}
# @return Getter method: attributes
#' @export
#' 
attribute.Modul <- function(x){
  return(x$attributes)
}

#' attribute<-
#' 
#' Set a nodes attributes
#' 
#' @rdname attribute
#' @export
#' 
'attribute<-' <- function(x, value, append, index){
  UseMethod('attribute<-', x)
}

#' @rdname attribute 
#' @param x Object of class \code{\link{Modul}}
#' @param value New Value
#' @param append Append attributes (TRUE) or replace existing ones (FALSE)
#' @param index Index of attribute to replace
#' @return Setter Method: object of class \code{\link{Modul}}
#' @export
#' @examples
#' 
#' m1<-Modul("Data")
#' 
#' attribute(m1)
#' 
#' attribute(m1)<-"test" #add an attribute without a name
#' attribute(m1)<-c(a2="test2") #add an attribute with a name
#' attribute(m1)
#' 
#' attribute(m1,append=FALSE)<-c(a1="attrib1") #replace the old attributes
#' attribute(m1)
#' 
#' attribute(m1)<-c(a2="attrib2",a3="attrib3") #add many attributes 
#' attribute(m1)
#' 
#' attribute(m1,index=2)<-c("replaced attribute") #replace second attibute
#' attribute(m1)
#' 
'attribute<-.Modul' <- function(x, value, append = T, index=NULL){
  if (is.null(index)){
    if (!append) {
      x$attributes <- value
    } else {
      x$attributes <- append(x$attributes,value)
    }
  }else { #replace at index
    x$attributes[[index]]<-value
  }
  return(x)  
}

#' children
#' 
#' Get child elements of a node
#' 
#' @seealso \code{\link{Modul}}, \code{\link{type}}, \code{\link{attribute}}
#' @export
#' 
children <- function(x){
  UseMethod('children', x)
}

#' @describeIn children
# @param x Object of class \code{\link{Modul}}
# @return Getter Method: list of children
#' @export
#' 
children.Modul <- function(x){
  if (length(x)>2){
    return (x[3:length(x)])
  }
  return(NULL)
}

#' children<-
#' 
#' Set child elements of a node
#' 
#' @rdname children
#' @export
#' 
'children<-' <- function(x,value,name){
  UseMethod('children<-', x)
}

#' @rdname children
#' @param x Object of class \code{\link{Modul}}
#' @param value New Value
#' @param name Name of the child element
#' @return Setter Method: object of class \code{\link{Modul}}
#' @export
#' @examples
#' data1<-Modul("Data")
#' model1<-Modul("Model")
#' map1<-Modul("Map")
#' map2<-Modul("Map")
#' 
#' children(data1,"map1")<-map1
#' children(data1,"map2")<-map2
#' children(model1,"data1")<-data1
#'
#' summary(model1)
#' 
'children<-.Modul' <- function(x, value, name=NULL){
  oldSize<-length(x)
  x[[oldSize+1]]<-value
  names(x)[[oldSize+1]]<-name
  return(x)
}

#' summary
#' 
#' Generic summary method
#' 
#' @param x Object of class \code{\link{Modul}}
#' 
summary <- function(x){
  UseMethod('summary',x)
}

#' summary.Modul
#'
#' Print a summary of the metadata tree.
#'
#' @param  x Object of class \code{\link{Modul}}
#' @seealso \code{\link{Modul}}
#' @export
summary.Modul <- function(x){
  cat(info(x,name=deparse(substitute(x))))
}


#generic method. Is called by summary method and calls itself recursively for each child object.
info <- function(x,indentation,name){
  UseMethod('info', x)
}


#implemenation for class Modul
info.Modul <- function(x,indentation = 0,name="unknown Name"){

  output = NULL
  indSeq<-"|\t"
  seperator<-paste("\n",paste(rep(indSeq,indentation),collapse=""),sep="")

  output<-paste(output,paste(x$type,name,sep=": "),sep=paste(rep(indSeq,indentation),collapse=""))

  if(!is.null(x$attributes)){ #show attributes
    output<-paste(output,"+ Attributes:",sep=seperator)
    for(i in 1:length(x$attributes)){
      newString=NULL
      if(toString(names(x$attributes)[i])==""){ #not a key value pair, just show the value
        newString<-x$attributes[i]
      } else { #it's a key value pair
        newString<-paste(c(names(x$attributes)[i],x$attributes[i]),collapse=" = ")
      }

      if(nchar(newString)>100){
        newString<-paste(c("|\t>",substr(newString,1,100)),collapse = " ")
        newString<-paste(newString,"...",sep=",")
      } else {
        newString<-paste(c("|\t>",newString),collapse = " ")
      }

      output<-paste(output,newString,sep=seperator) #add the entry (key value pair, or just a value)
    }
  }

  #process child elements
  if(length(x)>2){
    numberOfElements<-length(x)-2
    output<-paste(output,paste(c("+ nested elements:",toString(numberOfElements)),collapse = ""),sep=seperator)
    for(i in 3:length(x)){
      childName<-names(x[i])
      output<-paste(output,info(x[[i]],indentation+1,childName),sep=seperator)
    }
  }

  return(output)
}

#' toXML
#' 
#' Export to XML.
#' 
#' Please note: As the name of an element of class Module is stored in its parent element, the topmost element has no name.
#' By default, the local variable name is used for export to XML. An alternative name can be set using the name parameter.
#' 
#' @param x Object of class \code{\link{Modul}}
#' @param name Name of the parent node. As default, the Variablename is used.
#' @param file Path to the XML file
#' @seealso \code{\link{Modul}}, \code{\link{summary.Modul}}, \code{\link{parseXML}}, \code{\link{getFirstModuleName}}
#' @export
#' 
toXML <- function (x,name,file){
  UseMethod('toXML',x)
}  

#' @describeIn toXML
#' @export
#' 
toXML.Modul <- function(x,name=NULL,file=NULL){
  if(is.null(name)){
    name<-deparse(substitute(x))
  }

  content<-toXMLRec(x,name)

  root<-XML.xmlNode(name = "sdmMetadata")#, namespaceDefinitions = c("http://www.w3.org/2001/XMLSchema-instance", "noNamespaceSchemaLocation" = "sdm.xsd") )
  XML.xmlAttrs(root,T)<- c( "xmlns:xsi"="http://www.w3.org/2001/XMLSchema-instance", "xsi:noNamespaceSchemaLocation"="sdm.xsd")

  root<-append.xmlNode(root,content)
  if(is.null(file)){
    return(root)
  } else {
    saveXML(doc = root, file = file, indent = TRUE)
  }
}

#' toXMLRec
#' 
#' private helper function for toXML
#' 
#' @param x Node (of type Modul) to be exported in XML
#' @param name Name of parent node. By default, this is the variable name where the object is stored.
#' @return xml node
#' 
toXMLRec <- function(x,name=NULL){

  node <- XML::xmlNode(class(x)[[1]])
  if (is.null(name)) name="undefined"
  XML::xmlAttrs(node) <- c(Name = name, Type = type(x))

  #process attributes
  if(!is.null(x$attributes)){ 
    numberOfAttributes <- length(x$attributes)
    attrNode<-XML::xmlNode("Attributes")
    for(i in 1 : numberOfAttributes){
      key<-NULL 
      value<-NULL
      if(toString(names(x$attributes)[i])==""){ #not a key value pair, just show the value
        key<-paste("attrib",toString(i),sep="") #use a fake key in xml (attrib1, attrib2, attrib3, etc)
        value<-x$attributes[i]
      } else { #it's a key value pair
        key<-names(x$attributes[i])
        value<-x$attributes[i]
      }
      tmpAttributeNode<-XML::xmlNode("Attribute")
      tmpAttributeNode<-XML::append.xmlNode(tmpAttributeNode,xmlNode("Key",value=key))
      tmpAttributeNode<-XML::append.xmlNode(tmpAttributeNode,xmlNode("Value",value=value))
      attrNode<-XML::append.xmlNode(attrNode,tmpAttributeNode)  
    }
    node<-XML::append.xmlNode(node,attrNode)
  }

  #process nested children
  if (length(x)>2){
    for (i in 3 : length(x)){
      name=names(x[i])
      child<-toXMLRec(x[[i]],name=name)
      node<-XML::append.xmlNode(node,child)
    }  
  }

  return(node)
}


