# Set the precision to 16 digits:
options( digits = 16 );

#' Run benchmarks.
#'
#' @examples
#' main();
main <- function() {
	# Define benchmark parameters:
	name <- "besselj0";
	iterations <- 1000000L;
	repeats <- 3;

	#' Print the TAP version.
	#'
	#' @examples
	#' print_version();
	print_version <- function() {
		cat( "TAP version 13\n" );
	}

	#' Print the TAP summary.
	#'
	#' @param total Total number of tests.
	#' @param passing Total number of passing tests.
	#'
	#' @examples
	#' print_summary( 3, 3 );
	print_summary <- function( total, passing ) {
		cat( "#\n" );
		cat( paste0( "1..", total, "\n" ) ); # TAP plan
		cat( paste0( "# total ", total, "\n" ) );
		cat( paste0( "# pass  ", passing, "\n" ) );
		cat( "#\n" );
		cat( "# ok\n" );
	}

	#' Print benchmark results.
	#'
	#' @param iterations Number of iterations.
	#' @param elapsed Elapsed time in seconds.
	#'
	#' @examples
	#' print_results( 10000L, 0.131009101868 );
	print_results <- function( iterations, elapsed ) {
		rate <- iterations / elapsed;
		cat( "  ---\n" );
		cat( paste0( "  iterations: ", iterations, "\n" ) );
		cat( paste0( "  elapsed: ", elapsed, "\n" ) );
		cat( paste0( "  rate: ", rate, "\n" ) );
		cat( "  ...\n" );
	}

	#' Run a benchmark.
	#'
	#' ## Notes
	#'
	#' * We compute and return a total "elapsed" time, rather than the minimum
	#'   evaluation time, to match benchmark results in other languages (e.g.,
	#'   Python).
	#'
	#'
	#' @param iterations Number of Iterations.
	#' @return Elapsed time in seconds.
	#'
	#' @examples
	#' elapsed <- benchmark( 10000L );
	benchmark <- function( iterations ) {
		# Run the benchmarks:
		results <- microbenchmark::microbenchmark( besselJ( runif(1)*100.0, 0 ), times = iterations );

		# Sum all the raw timing results to get a total "elapsed" time:
		elapsed <- sum( results$time );

		# Convert the elapsed time from nanoseconds to seconds:
		elapsed <- elapsed / 1.0e9;

		return( elapsed );
	}

	print_version();
	for ( i in 1:repeats ) {
		cat( paste0( "# r::", name, "\n" ) );
		elapsed <- benchmark( iterations );
		print_results( iterations, elapsed );
		cat( paste0( "ok ", i, " benchmark finished", "\n" ) );
	}
	print_summary( repeats, repeats );
}

main();

cnn_convolve <- function(filter_dim, num_filters, images, w,b) {
  #options(digits = 2)
  num_images <- dim(images)[3]
  image_dim <- dim(images)[1]
  conv_dim <- image_dim - filter_dim+1
  col_dim <- dim(images)[2] - filter_dim +1
  convolved_features <- array(0, c(conv_dim, conv_dim, num_filters,
                                   num_images))
  for(image_num in 1:num_images) {
    for(filter_num in 1:num_filters) {
      convolved_image <- array(0, c(conv_dim, conv_dim))
      filters <- w
      x <- images[,,image_num]
      y <- filters[,,filter_num]
      for(i in 1:conv_dim) {
        for(j in 1: col_dim) {
          convolved_image[i,j] <- convolve(x[i:(i+filter_dim-1),
                                             j:(j+filter_dim-1)], y, 
                                           type = "filter")
        }
      }
      convolved_image <- convolved_image + b[filter_num]
      convolved_image <- tanh(convolved_image)

      convolved_features[,,filter_num, image_num] <- convolved_image
      #return(convolved_features)
    }
  }
}

# A number is pandigital if it is made up of the digits 1:n, where n is the total number
# of digits in the number. For example, 15234 is a 5-digit pandigital number b/c  it contains
# 1:5 and is 5 digits long. Each digit can only appear once.
#
# 7254 is a pandigital product in that 39 x 186 = 7254, where the number and two of its
#   factors are together 1:9 pandigital (i.e. contain 1:9 each exactly once)
#
# Find the sum of all products (e.g. 7254) which are pandigital products of 1:9

# Only logical possibilities for number of digits in each product and factors would be:
# x * xxxx = xxxx
# xx * xxx = xxxx

no_repeats <- function(x,missing_digits){
  # Returns BOOLEAN
  # TRUE if x has no repeats and no digit is 0
  # FALSE if x has repeat digits or any digits is 0
  r <- rep(TRUE,length(x))
  for(i in 1:length(x)){
    dont_match <- missing_digits
    while(x[i] > 0){
      mod <- x[i] %% 10
      if(mod == 0){
        r[i] <- FALSE
      }else if(sum(match(dont_match,mod),na.rm=TRUE) > 0){
        r[i] <- FALSE
      }
      dont_match <- c(dont_match,mod)
      x[i] <- x[i] %/% 10
    }
  }
  return(r)
}

digits <- function(x){
  d <- c()
  while(x > 0){
    d <- c(d,x %% 10)
    x <- x %/% 10
  }
  return(rev(d))
}

poss_products <- 1234:9876
poss_products <- poss_products[no_repeats(poss_products,c())]

poss_factors1 <- 2:98 # Factor can't be 1
poss_factors1 <- poss_factors1[no_repeats(poss_factors1,c())]

total <- 0
for(product in poss_products){
  is_pandigital <- FALSE
  used <- digits(product)
  f1 <- poss_factors1[no_repeats(poss_factors1,used)] # Remove factors w/ duplicates
  f1 <- f1[product %% f1 == 0] # Remove non-factors
  if(length(f1) > 0){ # If we have any left
    f2 <- product / f1

    for(i in 1:length(f1)){
      dig_f1 <- digits(f1[i])
      dig_f2 <- digits(f2[i])
      # Check that no digits repeat, but that every digit is used
      if(no_repeats(f2[i],c(used,dig_f1)) & length(c(used,dig_f1,dig_f2))==9){
        is_pandigital <- TRUE
      }
    }
  }
  if(is_pandigital){
    total <- total + product
  }
}
print(total)

"grasp.step.bic" <-
function (object, scope, scale, direction = c("both", "backward", 
    "forward"), trace = TRUE, keep = NULL, steps = 1000, ...) 
{
cat("#", "\n")
cat("# FUNCTION: grasp.step.bic", "\n")
cat("# (by Splus, adapted by A. Lehmann from step.gam)", "\n")
cat("# grasp.step.bic is a modified version of step.gam of Splus using BIC instead ",
"\n")
cat("# of AIC criteria", "\n")
cat("#", "\n")

 scope.char <- function(formula) {
        tt <- terms(formula)
        tl <- attr(tt, "term.labels")
        if (attr(tt, "intercept")) 
            c("1", tl)
        else tl
    }
    re.arrange <- function(keep) {
        namr <- names(k1 <- keep[[1]])
        namc <- names(keep)
        nc <- length(keep)
        nr <- length(k1)
        array(unlist(keep, recursive = FALSE), c(nr, nc), list(namr, 
            namc))
    }
    untangle.scope <- function(terms, regimens) {
        a <- attributes(terms)
        response <- deparse(a$variables[[2]])
        term.labels <- a$term.labels
        if (!is.null(a$offset)) {
            off1 <- deparse(a$variables[[a$offset]])
        }
        nt <- length(regimens)
        select <- integer(nt)
        for (i in seq(nt)) {
            j <- match(regimens[[i]], term.labels, 0)
            if (any(j)) {
                if (sum(j > 0) > 1) 
                  stop(paste("The elements of a regimen", i, 
                    "appear more than once in the initial model", 
                    sep = " "))
                select[i] <- seq(j)[j > 0]
                term.labels <- term.labels[-sum(j)]
            }
            else {
                if (!(j <- match("1", regimens[[i]], 0))) 
                  stop(paste("regimen", i, "does not appear in the initial model", 
                    sep = " "))
                select[i] <- j
            }
        }
        if (length(term.labels)) 
            term.labels <- paste(term.labels, "+")
        if (!is.null(a$offset)) 
            term.labels <- paste(off1, term.labels, sep = " + ")
        return(list(response = paste(response, term.labels, sep = " ~ "), 
            select = select))
    }
    make.step <- function(models, fit, scale, object) {
        chfrom <- sapply(models, "[[", "from")
        chfrom[chfrom == "1"] <- ""
        chto <- sapply(models, "[[", "to")
        chto[chto == "1"] <- ""
        dev <- sapply(models, "[[", "deviance")
        df <- sapply(models, "[[", "df.resid")
        ddev <- c(NA, diff(dev))
        ddf <- c(NA, diff(df))
        BIC <- sapply(models, "[[", "BIC")
        heading <- c("Stepwise Model Path \nAnalysis of Deviance Table", 
            "\nInitial Model:", deparse(as.vector(formula(object))), 
            "\nFinal Model:", deparse(as.vector(formula(fit))), 
            paste("\nScale: ", format(scale), "\n", sep = ""))
        aod <- data.frame(From = chfrom, To = chto, Df = ddf, 
            Deviance = ddev, "Resid. Df" = df, "Resid. Dev" = dev, 
            BIC = BIC, check.names = FALSE)
        fit$anova <- as.anova(aod, heading)
        fit
    }
    direction <- match.arg(direction)
    if (missing(scope)) 
        stop("you must supply a scope argument to step.gam(); the gam.scope() function might be useful")
    if (!is.character(scope[[1]])) 
        scope <- lapply(scope, scope.char)
    response <- untangle.scope(object$terms, scope)
    form.y <- response$response
    backward <- direction == "both" | direction == "backward"
    forward <- direction == "both" | direction == "forward"
    items <- response$select
    family <- family(object)
    Call <- object$call
    term.lengths <- sapply(scope, length)
    n.items <- length(items)
    visited <- array(FALSE, term.lengths)
    visited[array(items, c(1, n.items))] <- TRUE
    if (!is.null(keep)) {
        keep.list <- vector("list", length(visited))
        nv <- 1
    }
    models <- vector("list", length(visited))
    nm <- 2
    form.vector <- character(n.items)
    for (i in seq(n.items)) form.vector[i] <- scope[[i]][items[i]]
    form <- deparse(object$formula)
    if (trace) 
        cat("Start: ", form)
    fit <- object
    n <- length(fit$fitted)
    if (missing(scale)) {
        famname <- family$family["name"]
        scale <- switch(famname, Poisson = 1, Binomial = 1, deviance.lm(fit)/fit$df.resid)
    }
    else if (scale == 0) 
      scale <- deviance.lm(fit)/fit$df.resid
      ##bBIC <- fit$BIC
scale <- summary(fit)$dispersion
bBIC <- deviance(fit) + scale * (n - fit$df.resid) * log(n)
print(paste("BIC: ", bBIC))
    if (trace) 
        cat("; BIC=", format(round(bBIC, 4)), "\n")
    models[[1]] <- list(deviance = deviance(fit), df.resid = fit$df.resid, 
        BIC = bBIC, from = "", to = "")
    if (!is.null(keep)) {
        keep.list[[nv]] <- keep(fit, bBIC)
        nv <- nv + 1
    }
    BIC <- bBIC + 1
    while (bBIC < BIC & steps > 0) {
        steps <- steps - 1
        BIC <- bBIC
        bitems <- items
        bfit <- fit
        for (i in seq(n.items)) {
            if (backward) {
                trial <- items
                trial[i] <- trial[i] - 1
                if (trial[i] > 0 && !visited[array(trial, c(1, 
                  n.items))]) {
                  visited[array(trial, c(1, n.items))] <- TRUE
                  tform.vector <- form.vector
                  tform.vector[i] <- scope[[i]][trial[i]]
                  form <- paste(form.y, paste(tform.vector, collapse = " + "))
                  if (trace) 
                    cat("Trial: ", form)
                  tfit <- update(object, eval(parse(text = form)), 
                    trace = FALSE, ...)
                  # tBIC <- tfit$BIC
tBIC<-deviance(tfit) + scale * (n - tfit$df.resid) * log(n)

                  if (!is.null(keep)) {
                    keep.list[[nv]] <- keep(tfit, tBIC)
                    nv <- nv + 1
                  }
                  if (tBIC < bBIC) {
                    bBIC <- tBIC
                    bitems <- trial
                    bfit <- tfit
                    bform.vector <- tform.vector
                    bfrom <- form.vector[i]
                    bto <- tform.vector[i]
                  }
                  if (trace) 
                    cat("; BIC=", format(round(tBIC, 4)), "\n")
                }
            }
            if (forward) {
                trial <- items
                trial[i] <- trial[i] + 1
                if (trial[i] <= term.lengths[i] && !visited[array(trial, 
                  c(1, n.items))]) {
                  visited[array(trial, c(1, n.items))] <- TRUE
                  tform.vector <- form.vector
                  tform.vector[i] <- scope[[i]][trial[i]]
                  form <- paste(form.y, paste(tform.vector, collapse = " + "))
                  if (trace) 
                    cat("Trial: ", form)
                  tfit <- update(object, eval(parse(text = form)), 
                    trace = FALSE, ...)
                  # tBIC <- tfit$BIC
tBIC<-deviance(tfit) + scale * (n - tfit$df.resid) * log(n)

                  if (!is.null(keep)) {
                    keep.list[[nv]] <- keep(tfit, tBIC)
                    nv <- nv + 1
                  }
                  if (tBIC < bBIC) {
                    bBIC <- tBIC
                    bitems <- trial
                    bfit <- tfit
                    bform.vector <- tform.vector
                    bfrom <- form.vector[i]
                    bto <- tform.vector[i]
                  }
                  if (trace) 
                    cat("; BIC=", format(round(tBIC, 4)), "\n")
                }
            }
        }
        if (bBIC >= BIC | steps == 0) {
            if (!is.null(keep)) 
                fit$keep <- re.arrange(keep.list[seq(nv - 1)])
            return(make.step(models[seq(nm - 1)], fit, scale, 
                object))
        }
        else {
            if (trace) 
                cat("Step : ", deparse(bfit$formula), "; BIC=", 
                  format(round(bBIC, 4)), "\n\n")
            items <- bitems
            models[[nm]] <- list(deviance = deviance(bfit), df.resid = bfit$df.resid, 
                BIC = bBIC, from = bfrom, to = bto)
            nm <- nm + 1
            fit <- bfit
            form.vector <- bform.vector
        }
    }
}

