requireWebshot2 <- function() {
  suppressMessages(res <- requireNamespace("webshot2", quietly = TRUE))
  res
}

snapshot3d <- function(filename = tempfile(fileext = ".png"), 
                       fmt = "png", top = TRUE, ..., scene, width = NULL, height = NULL,
                       webshot = TRUE) {
  force(filename)

  if (webshot && !requireWebshot2()) {
    warning("webshot = TRUE requires the webshot2 package; using rgl.snapshot() instead")
    webshot <- FALSE
  }
  saveopts <- options(rgl.useNULL = webshot)
  on.exit(options(saveopts))

    # The logic here is a little convoluted:
  # scene  resize  webshot getscene1 plot resize getscene2
  # no     no      no      
  # no     no      yes         X
  # no     yes     no                       X
  # no     yes     yes         X      X     X      X                 
  # yes    no      no                 X
  # yes    no      yes     
  # yes    yes     no                 X     X
  # yes    yes     yes                X     X      X


  resize <- !is.null(width) || !is.null(height)
  havescene <- !missing(scene)

  if (havescene) {
    if (inherits(scene, "rglWebGL")) {
      snapshot <- scene$x$snapshot
      if (!is.null(snapshot) && is.null(width) && is.null(height))
        return(saveURI(snapshot, filename))
      else
        scene <- attr(scene, "origScene")
    }
  }
  if (!havescene && webshot)
    scene <- scene3d()

  if ((!havescene && resize && webshot)
      || (havescene && (resize || !webshot))) {
    open3d()
    plot3d(scene)
    on.exit(close3d(), add = TRUE)
  }
  if (resize) {
    saverect <- rect <- par3d("windowRect")
    on.exit(par3d(windowRect = saverect), add = TRUE)
    if (!is.null(width))
      rect[3] <- rect[1] + width
    if (!is.null(height))
      rect[4] <- rect[2] + height
    par3d(windowRect = rect)
  }
  if (webshot) {
    if (resize)
      scene <- scene3d()
    rect <- par3d("windowRect")
    f1 <- tempfile(fileext = ".html")
    on.exit(unlink(f1), add = TRUE)
    width <- rect[3] - rect[1]
    height <- rect[4] - rect[2]
    saveWidget(rglwidget(scene,
                         elementId = "webshot",
                         width = width,
                         height = height,
                         webgl = TRUE), 
               f1)
    capture.output(webshot2::webshot(f1, file = filename, selector = "#webshot",
                        vwidth = width + 100, vheight = height, ...),
                   type = "message")
    invisible(filename)
  } else
    rgl.snapshot(filename, fmt, top)
}

### Constructor (Accepts either controlPoints or parameters)
"SimilarityTransformation" <- function(controlPoints=data.frame(), parameters=numeric()) {
    if (missing(parameters))
        return(new("SimilarityTransformation", controlPoints=controlPoints))
    if (missing(controlPoints))
        return(new("SimilarityTransformation", parameters=parameters))
    new("SimilarityTransformation", controlPoints=controlPoints, parameters=parameters)
}

### Calculate parameters from control points
###     Modifies the original object
### Arguments:
### - object is an "SimilarityTransformation" object
###
if (!isGeneric("calculateParameters"))
    setGeneric("calculateParameters",function(object){standardGeneric ("calculateParameters")})
setMethod(f="calculateParameters",signature(object="SimilarityTransformation"),
    definition=function(object){
		if (ncol(object@controlPoints) == 0)
			stop("Control points were not provided. You could access the parameters directly by calling 'getParameters'.")

        newObject <- deparse(substitute(object))

        x1=c(object@controlPoints[,1],object@controlPoints[,2])
        x2=c(object@controlPoints[,2],-object@controlPoints[,1])
        ones = rep(1,nrow(object@controlPoints))
        zeros = rep(0,nrow(object@controlPoints))
        x3=c(ones,zeros)
        x4=c(zeros,ones)
        linMod=lm(formula=c(object@controlPoints[,3],object@controlPoints[,4]) ~ x1 + x2 + x3 + x4 - 1)

        object@parameters <- as.vector(coef(linMod))
        object@residuals <- matrix(linMod$residuals,nrow(object@controlPoints))
        object@rmse <- sqrt(sum(object@residuals**2)/nrow(object@residuals))

        assign(newObject,object,envir=parent.frame())
		return(invisible())
	}
)

