# ICM (regression)
#   See output$b, output$var_y, ...
ICM_Regression <- function(
    user, item, y, x, w, z,
    alpha, beta, u, v,
    debug=0, lm=F,...
){
    nObs     = length(y);
    nUsers   = length(alpha);
    nItems   = length(beta);
    nFactors = ncol(u);

    output = list();

    # determine b and var_y
    target = y - alpha[user] - beta[item] - apply(u[user,,drop=FALSE] * v[item,,drop=FALSE], 1, sum);
    if(all(x == 0)){
        fit = list(fitted.values=0, coefficients=rep(0,ncol(x)));
    }else{
        fit = lm(target ~ x -1, model=FALSE);
    }
    if(length(fit$coef) != ncol(x)) stop("length(fit$coef) != ncol(x)");
    rss = sum((fit$fitted.values - target)^2);

    output$b = fit$coefficients;
    output$var_y = rss / nObs;

    # determin g0 and var_alpha
    fit = fit.forMainEffect.bayesglm(alpha, w,lm=lm, ...);
    output$g0 = fit$coef;
    output$var_alpha = sum(fit$rss) / nUsers;

    # determin d0 and var_beta
    fit = fit.forMainEffect.bayesglm(beta, z,lm=lm, ...);
    output$d0 = fit$coef;
    output$var_beta = sum(fit$rss) / nItems;

    # determin G and var_u
    fit = fit.forFactors.bayesglm(u, w, lm=lm,...);
    output$G = fit$coef;
    output$var_u = sum(fit$rss) / (nUsers * nFactors);

    # determin D and var_v
    fit = fit.forFactors.bayesglm(v, z, lm=lm,...);
    output$D = fit$coef;
    output$var_v = sum(fit$rss) / (nItems * nFactors);

    return(output);
}

MC_MStep_logistic_arscid <- function(
    user, item, y, x, b, w, z,
    o, alpha, alpha.sumvar, beta, beta.sumvar, u, u.sumvar, v, v.sumvar,
    debug=0, lm=F, use.glmnet=F, fit.ars.alpha=F, fit.regression=T,
    beta.int=T, main.effects=F, ...
){
    nObs     = length(y);
    nUsers   = length(alpha);
    nItems   = length(beta);
    nFactors = ncol(u);

    output = list();

    # find ars alpha ...
    if (fit.ars.alpha )
    {
        ars_alpha = estalpha(y,b,o);
        output$ars_alpha = ars_alpha;
    }
    else
    {
        output$ars_alpha = 0.5;
        ars_alpha = 0.5;
    }

    # determine b and var_y
    if(beta.int == F){
      if(all(x == 0)){
        fit = list(fitted.values=0, coefficients=rep(0,ncol(x)));
      }else{
        if(use.glmnet){
            if (all(x[,1] == 1))
            {
                x.new = Matrix(x[,2:ncol(x)],sparse=TRUE);
            } else {
                stop("feature should have a constant term for intercept");
            }
            fit0 = cv.glmnet(x=x.new, y=y, offset=o, nfolds=3, family="binomial");
            lambdaind = which(fit0$lambda==fit0$lambda.min);
            a0 = fit0$glmnet.fit$a0[lambdaind];
            coef = fit0$glmnet.fit$beta[,lambdaind];
            fit = list();
            fit$coefficients = as.vector(c(a0,coef));
        } else {
            # fit as covariate
            nobs = length(x)
            x = cbind(matrix(x,length(x),1), rep(0,length(x)))

            fit = bayesglm(y ~ x - 1, family=binomial(link="logit"), offset=o ,model=F, prior.scale = 5);

            fit$coef = fit$coef[1]; fit$coefficients=fit$coefficients[1]; x = matrix(x[,1],nobs,1)
        }
      }
      output$b = fit$coefficients;
    } else {
      #fit in random effects heirarchy inside of beta ... but still center for computation ...
      output$b = b;
    }

    if (fit.ars.alpha && ncol(as.matrix(x))>1)
    {
        stop("Currently fit.ars.alpha=T only works for ncol(x)==1");
    }
    cat("fit regression=",fit.regression,"\n")
    cat("intercept in beta prior =",beta.int,"\n")

    #If fit.regression=T do the normal thing, if not just find vars
    if(fit.regression){
      # determin g0 and var_alpha
      if (use.glmnet==F  ) {
          fit = fit.forMainEffect.bayesglm(alpha, w, lm=lm,...);
      } else {
        fit = fit.forMainEffect.glmnet(alpha, w, ...);
      }
      output$g0 = fit$coef;
      output$var_alpha = (sum(fit$rss) + alpha.sumvar) / nUsers;

      # determin d0 and var_beta ( and b if in heirarchy )
      if (beta.int) z2 = cbind(1,z) else z2=z

      if (use.glmnet==F) {
          fit = fit.forMainEffect.bayesglm(beta, z2, lm=lm,...);
      } else {
        fit = fit.forMainEffect.glmnet(beta, z2, ...);
      }

      output$d0 = fit$coef;
      output$var_beta = (sum(fit$rss) + beta.sumvar) / nItems;

      if(!main.effects){
                                        # determin G and var_u
        if (use.glmnet==F)
          {
            fit = fit.forFactors.bayesglm(u, w, lm=lm,...);
          } else
        {
          fit = fit.forFactors.glmnet(u, w, ...);
        }
        output$G = fit$coef;

	      # Whether or not identifiable==T, var_u should always be 1
	      output$var_u = rep(1,nFactors);

        # determin D and var_v
        if (use.glmnet==F)
          {
            fit = fit.forFactors.bayesglm(v, z, lm=lm, ...);
          } else
        {
          fit = fit.forFactors.glmnet(v, z, ...);
        }
        output$D = fit$coef;
        cat("var_v.rss=",fit$rss,"\n");
        cat("v.sumvar=",v.sumvar,"\n");
        output$var_v = (fit$rss + v.sumvar)/nItems;
      }
    } else
    {
      output$var_alpha = (sum(alpha^2) + alpha.sumvar) / nUsers;
      output$var_beta = (sum(beta^2) + beta.sumvar) / nItems;
      output$var_u = rep(1,nFactors);
      if (!main.effects) output$var_v = (apply(v^2,2,sum)+v.sumvar)/nItems;
    }
    if( beta.int == T && fit.regression == F)
    {
        output$d0 = rep(0,dim(z)[2]); output$d0[1] = mean(beta);
        output$var_alpha = (sum(alpha^2) + alpha.sumvar) / nUsers;
        output$var_beta = (sum((beta - mean(beta))^2) + beta.sumvar) / nItems;
        output$var_u = rep(1,nFactors);
        if (!main.effects) output$var_v = (apply(v^2,2,sum)+v.sumvar)/nItems;
    }

    return(output);
}

thin <- function (x, thin) {
  ## Thins the data in x, so that every thin'th observation is
  ## returned.  This is useful for making plots of large MCMC objects,
  ## where overplotting or memory constraints make plotting the whole
  ## object undesirable.
  ##
  ## Args:
  ##   x: A numeric vector, matrix, or array to be thinned.  If a
  ##     matrix or array then the first dimension corresponds to the
  ##     observation number, and will be used for thinning.
  ##   thin: The frequency with which to thin.  E.g. if thin == 10
  ##     then every 10th observation will be returned.
  ##
  ## Returns:
  ##    The thinned subset of x.
  stopifnot(is.numeric(thin) && length(thin) == 1)
  if (thin <= 1)
    return(x)
  if (is.array(x)) {
    nr <- dim(x)[1]
  } else if (is.numeric(x)) {
    nr <- length(x)
  } else stop("x must be a numeric type in thin()")

  top <- floor(nr/thin)
  indx <- (1:top) * thin

  if (is.matrix(x)) {
    return(x[indx, ])
  } else if (is.numeric(x)) {
    return(x[indx])
  } else if (is.array(x)) {
    stop("how do you drop the first N sub-components of an array?")
  }
}

getLabeledNodes = function(graph, .label, ...) UseMethod("getLabeledNodes")

getLabeledNodes.default = function(x, ...) {
  stop("Invalid object. Must supply graph object.")
}

getLabeledNodes.graph = function(graph, .label, ...) {
  stopifnot(is.character(.label))

  url = paste(attr(graph, "root"), "label", .label, "nodes", sep = "/")
  param = c(...)

  if(length(param) > 1)
    stop("Can only search by one property.")

  if(length(param) == 1) {
    url = paste0(url, "?", names(param), "=")

    if(is.character(param[[1]])) {
      param[[1]] = URLencode(param[[1]], reserved = TRUE)
      url = paste0(url, "%22", param[[1]], "%22")
    } else if(is.numeric(param[[1]])) {
      url = paste0(url, param[[1]])
    } else if(is.logical(param[[1]])) {
      if(param[[1]]) {
        url = paste0(url, "true")
      } else {
        url = paste0(url, "false")
      }
    } else {
      stop("Property value must be character, numeric, or logical.")
    }
  }

  result = http_request(url, "GET", graph)

  if(length(result) == 0) {
    return(invisible())
  }

  nodes = lapply(result, function(r) configure_result(r, attr(graph, "username"), attr(graph, "password"), attr(graph, "auth_token")))
  return(nodes)
}

