#' Check fit for VAST model
#'
#' \code{check_fit} checks bounds and throws an informative message if any look bad
#'
#' If `check_fit` identifies an issue in estimated parameters, then the model structure should typically be changed.
#' Recommended model changes differ somewhat for univariate and multivariate models as explained below.
#'
#' For univariate models:
#' \itemize{
#' \item If `ln_H_input` are approaching extreme values (i.e., > 5 or < -5), then turn consider turning off anisotropy, `make_data(..., Aniso=FALSE)`
#' \item If `L_beta1_z` is approaching zero (i.e., +/- 0.001), then turn off random-effects for temporal variation in the first intercept `RhoConfig["Beta1"]=3`
#' \item If `L_beta2_z` is approaching zero (i.e., +/- 0.001), then turn off random-effects for temporal variation in the first intercept `RhoConfig["Beta2"]=3`
#' \item If `L_omega1_z` is approaching zero (i.e., +/- 0.001), then turn off spatial effects for the 1st linear predictor `FieldConfig["Omega1"]=0`
#' \item If `L_omega2_z` is approaching zero (i.e., +/- 0.001), then turn off spatial effects for the 1st second predictor `FieldConfig["Omega2"]=0`
#' \item If `L_epsilon1_z` is approaching zero (i.e., +/- 0.001), then turn off spatio-temporal effects for the 1st linear predictor `FieldConfig["Epsilon1"]=0`
#' \item If `L_epsilon2_z` is approaching zero (i.e., +/- 0.001), then turn off spatio-temporal effects for the 1st second predictor `FieldConfig["Epsilon2"]=0`
#' \item If `Beta_rho1_f` is approaching one (i.e., > 0.999), then turn consider reducing to a random-walk structure for the intercept of the 1st linear predictor `RhoConfig["Beta1"]=2`
#' \item If `Beta_rho2_f` is approaching one (i.e., > 0.999), then turn consider reducing to a random-walk structure for the intercept of the 2st linear predictor `RhoConfig["Beta2"]=2`
#' \item If `Epsilon_rho1_f` is approaching one (i.e., > 0.999), then turn consider reducing to a random-walk structure for spatio-temporal variation of the 1st linear predictor `RhoConfig["Epsilon1"]=2`
#' \item If `Epsilon_rho2_f` is approaching one (i.e., > 0.999), then turn consider reducing to a random-walk structure for spatio-temporal variation of the 2nd linear predictor `RhoConfig["Epsilon2"]=2`
#' }
#'
#' For multivariate models, these same principles apply, but there are more options to simplify model structure.
#' For example, if any `L_beta1_z` is approaching zero (i.e., +/- 0.001), then consider using `fit_model(...,Map=[custom-map])` to turn off individual parameters;
#' or if using a factor model then reduce the number of factors by decreasing FieldConfig["Beta1"]`
#'
#' @param parameter_estimates output from \code{\link[TMBhelper]{fit_tmb}}
#' @param check_gradients Boolean stating whether to check bounds as well as other issues
#' @param quiet Boolean stating whether to print warnings to terminal
#' @return Did an automated check find an obvious problem code (TRUE is bad; FALSE is good)
#'
#' @export
#' @md
# Using https://cran.r-project.org/web/packages/roxygen2/vignettes/rd-formatting.html for guidance on markdown-enabled documentation
check_fit = function( parameter_estimates, check_gradients=FALSE, quiet=FALSE ){

  # Initialize code for good model
  problem_found = FALSE

  # Check for informative bounds
  On_bounds = ifelse( parameter_estimates$par<(parameter_estimates$diagnostics[,'Lower']+0.0001) | parameter_estimates$par>(parameter_estimates$diagnostics[,'Upper']-0.0001), TRUE, FALSE )
  if( any(On_bounds) ){
    problem_found = TRUE
    if(quiet==FALSE){
      message("\nCheck bounds for the following parameters:")
      print( parameter_estimates$diagnostics[which(On_bounds),] )
    }
  }

  # Check for stuff at zero
  At_zero = ifelse( abs(parameter_estimates$par) < 0.0001, TRUE, FALSE )
  if( any(At_zero) ){
    problem_found = TRUE
    if(quiet==FALSE){
      message("\nThe following parameters appear to be approaching zero:")
      print( parameter_estimates$diagnostics[which(At_zero),] )
      if( length(grep( "L_", names(parameter_estimates$par[which(At_zero)]))) ){
        message("Please turn off factor-model variance parameters `L_` that are approaching zero and re-run the model")
      }
    }
  }

  # Check bad gradients
  Bad_gradient = ifelse( abs(parameter_estimates$diagnostics[,'final_gradient']) > 0.001, TRUE, FALSE )
  if( check_gradients==TRUE && any(Bad_gradient) ){
    problem_found = TRUE
    if(quiet==FALSE){
      message("\nThe following parameters has a bad final gradient:")
      print( parameter_estimates$diagnostics[which(Bad_gradient),] )
    }
  }

  # Return
  return( invisible(problem_found) )
}

#' T2 model and score
#'
#' @param data    vector, matrix, or data.frame.
#' @param cluster list.  kmeans cluster model
#' @param plot    logical. plot or not
#' @param alpha   numeric. critical index
#'
#' @return list(fit=list(cluster, icovs, alpha, ucl), score)
#' @export
#'
#' @examples el.t2(iris[,-5], el.kmeans(iris[,-5], 3)$fit)
#' 
el.t2 <- function(data, cluster = NULL, alpha = 0.05, plot = TRUE) {

  if (is.vector(data)) {
    if (!el.isValid(data, 'single')) return()
    d <- data[!is.na(data)]
    d <- as.matrix(d)
  } else{
    if (!el.isValid(data, 'multiple')) return()
    d <- data[stats::complete.cases(data), ]
  }

  if (nrow(d) < 1) {
    logger.warn("There's no non-NA data.")
    return()
  }

  if (is.null(cluster)) {
    cluster <- list(k = 1, center = as.matrix(stats::kmeans(d, 1)$center, nrow = 1))
    clusterScore <- rep(1, nrow(d))
  } else{
    clusterScore <- el.kmeansScore(d, cluster)
  }

  icovs <- lapply(1:cluster$k, function(i) {
    d1 <- d[clusterScore == i, ]
    if (nrow(d1) <= ncol(d1)) {
      logger.warn("Too small cluster: %d", i)
      NA
    } else{
      el.inv(stats::cov(d1))
    }
  })

  fit <- list(cluster = cluster, icovs = icovs)
  score <- el.t2Score(data, fit, FALSE)
  ucl <- el.limit(score, alpha)
  fit <- c(fit, list(alpha = alpha, ucl = ucl))

  if(plot){
    oldPar <- graphics::par(no.readonly = T)
    plot(score, ylab='T2 score', type='l')
    graphics::abline(h = fit$ucl, col='red')
    graphics::par(oldPar)
  }

  list(fit = fit, score=score)
}


#' T2 score given T2 model
#'
#' @param data    vector, matrix, or data.frame.
#' @param fit     list(cluster, icovs, alpha, ucl). T2 model 
#' @param plot    logical. plot or not
#'
#' @return vector. T2 scores
#' @export
#'
#' @examples el.t2Score(iris[,-5], el.t2(iris[,-5], el.kmeans(iris[,-5], 3)$fit)$fit)
#' 
el.t2Score <- function(data, fit, plot = TRUE) {

  if(is.vector(data)){
    if(!el.isValid(data, 'single')) return()
    d <- as.matrix(data)
  }else{
    if(!el.isValid(data, 'multiple')) return()
    d <- as.matrix(data)
  }

  clus <- el.kmeansScore(d, fit$cluster)

  res <- sapply(1:nrow(d), function(i){

    if(sum(is.na(d[i])) > 0)
      NA
    else{
      cl <- clus[i]

      if(is.na(cl) | ! is.matrix(fit$icovs[[cl]]))
        NA
      else{
        v <- as.vector(d[i,]) - as.vector(fit$cluster$center[cl,])
        v %*% fit$icovs[[cl]] %*% v
      }
    }
  })

  if(plot){
    oldPar <- graphics::par(no.readonly = T)
    plot(res, ylab='T2 score', type='l')
    graphics::abline(h = fit$ucl, col='red')
    graphics::par(oldPar)
  }

  res
}

lines_midpoints <- function (sldf) 
{
	#stopifnot(is.projected(sldf))
	Lns <- slot(sldf, "lines")
	hash_lns <- sapply(Lns, function(x) length(slot(x, "Lines")))
	N <- sum(hash_lns)
	midpoints <- matrix(NA, ncol = 2, nrow = N)
	Ind <- integer(length = N)
	ii <- 1
	for (i in 1:length(Lns)) {
		Lnsi <- slot(Lns[[i]], "Lines")
		for (j in 1:hash_lns[i]) {
			Ind[ii] <- i
			midpoints[ii, ] <- get_midpoint(slot(Lnsi[[j]], "coords"))
			ii <- ii + 1
		}
	}
	if (is(sldf, "SpatialLinesDataFrame")) {
		df0 <- slot(sldf, "data")[Ind, ]
		df <- as.data.frame(cbind(df0, Ind))
	}
	else df <- data.frame(Ind = Ind)
	spdf <- SpatialPointsDataFrame(midpoints, data = df, proj4string = CRS(proj4string(sldf)))
	return(spdf)
}

get_midpoint <- function (coords) {
	dist <- sqrt((diff(coords[, 1])^2 + (diff(coords[, 2]))^2))
	dist_mid <- sum(dist)/2
	dist_cum <- c(0, cumsum(dist))
	end_index <- which(dist_cum > dist_mid)[1]
	start_index <- end_index - 1
	start <- coords[start_index, ]
	end <- coords[end_index, ]
	dist_remaining <- dist_mid - dist_cum[start_index]
	start + (end - start) * (dist_remaining/dist[start_index])
}

